const add=document.querySelector(".clients__add"),popup=document.querySelector(".popup");add.addEventListener("click",()=>{popup.style.opacity="1",popup.style.pointerEvents="all"});const close=document.querySelector(".popup__close");close.addEventListener("click",()=>{popup.style.opacity="0",popup.style.pointerEvents="none"});const menuLink=document.querySelectorAll(".nav__link");menuLink.forEach(e=>{e.addEventListener("click",t=>{t.preventDefault(),menuLink.forEach(e=>{e.classList.remove("active")}),e.classList.add("active")})});const leftIcon=document.querySelectorAll(".left__icon");leftIcon.forEach(e=>{e.addEventListener("click",t=>{t.preventDefault(),leftIcon.forEach(e=>{e.classList.remove("active")}),e.classList.add("active")})});