const add=document.querySelector(".clients__add"),popup=document.querySelector(".popup");add.addEventListener("click",()=>{popup.style.opacity="1",popup.style.pointerEvents="all"});const close=document.querySelector(".popup__close");function closePopup(){popup.style.opacity="0",popup.style.pointerEvents="none"}close.addEventListener("click",closePopup);const cancel=document.querySelector(".popup__cancel");cancel.addEventListener("click",closePopup);const menuLink=document.querySelectorAll(".nav__link");menuLink.forEach(e=>{e.addEventListener("click",c=>{c.preventDefault(),menuLink.forEach(e=>{e.classList.remove("active")}),e.classList.add("active")})});const leftIcon=document.querySelectorAll(".left__icon");leftIcon.forEach(e=>{e.addEventListener("click",c=>{c.preventDefault(),leftIcon.forEach(e=>{e.classList.remove("active")}),e.classList.add("active")})});